mapper.select:
SELECT id FROM student_tb
WHERE name = #{name}
AND birth_date = #{birthDate}
AND gender = #{gender}
AND address =#{address}
AND tel = #{tel}
AND dept_id = #{deptId}
AND entrance_date = #{entranceDate}
AND email = #{email}

SELECT address, tel, email
FROM student_tb
WHERE id = #{userId}

mapper.update:
UPDATE student_tb SET
address = #{address}, tel = #{tel}, email = #{email}
WHERE id = #{userId}
