mapper.select:
SELECT id FROM professor_tb 
WHERE name = #{name}
AND birth_date = #{birthDate}
AND gender = #{gender}
AND address =#{address}
AND tel = #{tel}
AND dept_id = #{deptId}
AND email = #{email}

SELECT address, tel, email
FROM professor_tb
WHERE id = #{userId}

mapper.update:
UPDATE professor_tb SET
address = #{address}, tel = #{tel}, email = #{email}
WHERE id = #{userId}
